import React from 'react'
import styled, { css } from 'styled-components'
import { Thumbnail, H3 } from './StyledElements'
import { FaGithub } from 'react-icons/fa'

const Project = styled.div`
    figure {
        border-radius: 1rem;
        position: relative;
        height: 250px;
        box-shadow: var(--shadow-light);
        overflow: hidden;
    };

    .link-btn {
        position: absolute;
        bottom: 0rem;
        font-size: 2rem;
        border-radius: 100px;
    };

    .right {
        background-color: black;
        right: 0rem;
        color: white;
        font-size: 2rem;
        border-radius: 100px;
        height: 2.5rem;
        width: 2.5rem;
        display: flex;
        align-items: center;
        justify-content: center;
    }

    .left{
        font-size: 1.2rem;
        background-color: rgb(20, 20, 20);
        color: white;
        text-decoration: none;
        padding: .2rem .6rem;
        padding-left: 2rem;

        display: inline-block;
        left: -1rem;
        height: auto;
        width: auto;
    }
`

const Img = styled(Thumbnail)`
    grid-area: img;
height: 100%;`



const Title = styled(H3)`
    display: inline-block;
`
const Technologies = styled.div`

`

const Description = styled.div`

`

export default function ProjectCard ({project}) {
    const {title, description, imageURL, liveLink, gitLink} = project;
    console.log(project)
    return (
        <Project>
            <figure>
                <Img src={imageURL} />
                <a className="link-btn right" target="_blank" rel="noopener noreferrer" href={gitLink}><FaGithub /></a>
                <a className="link-btn left" target="_blank" rel="noopener noreferrer" href={liveLink}>Live Demo</a>
            </figure>
            <div>

                <Title>{title}</Title>


            </div>
            <Technologies></Technologies>

            <Description>{description}</Description>
        </Project>
    )
}
