import styled from 'styled-components'

export const Button = styled.button.attrs(props => {
    let bg, fg;
    bg = props.background || props.theme.bg
    fg = props.color || props.theme.fg
    let size = '.5rem .7rem';
    if (props.big) {size = '1rem 1.5rem'}
    if (props.small) {size = '.2rem .4rem'}

    return {
        size,
        fg,
        bg
    }
    })`
    padding: ${props => props.size};
    color: ${props => props.primary ? props.bg : props.fg};
    background: ${props => props.primary ? props.fg :  props.bg};
    border: 2px solid ${props => props.fg};
    cursor: pointer;
    border-radius: 1000px;
    margin: 1rem;
    text-decoration: none;
    outline: 0;
    box-shadow: 0 2px 2px red;

    `
