import React from 'react'
import styled, { css } from 'styled-components'
import { Thumbnail, H3 } from './StyledElements'
import { FaGithub } from 'react-icons/fa'

const Project = styled.div`
    figure {
        border-radius: 1rem;
        position: relative;
        overflow: hidden;
        height: 250px;
        box-shadow: var(--shadow-light)
    }

    .git-btn {
        position: absolute;
        background-color: black;
        bottom: 0px;
        right: 0;
        color: white;
        font-size: 1.4rem;
        padding: 1rem;
        border-radius: 100px;
        height: 50px;
        width: 50px;
        display: flex;
        align-items: center;
        justify-items: center;
    }
`

const Img = styled(Thumbnail)`
    grid-area: img;
height: 100%`

const LinkBtn = styled.span`
    background: transparent;
    a {
        background-color: black;
        color: white;
        padding: .2rem 1rem;
        border-radius: 1rem;


        }

`


const Title = styled(H3)`
    display: inline-block;
`
const Technologies = styled.div`

`

const Description = styled.div`

`

export default function ProjectCard ({project}) {
    const {title, description, imageURL, liveLink, gitLink} = project;
    console.log(project)
    return (
        <Project>
            <figure>
                <Img src={imageURL} />
                <a className="git-btn" target="_blank" rel="noopener noreferrer" href={gitLink}><FaGithub /></a>
            </figure>
            <div>

                <Title>{title}</Title>
                <LinkBtn left ><a target="_blank" rel="noopener noreferrer" href={liveLink}>Live Demo</a></LinkBtn>
                <LinkBtn righ ></LinkBtn>

            </div>
            <Technologies></Technologies>

            <Description>{description}</Description>
        </Project>
    )
}
