import React from 'react'
import styled, { css } from 'styled-components'
import { Thumbnail, H3 } from './StyledElements'
import { FaGithub, FaHtml5, FaSass, FaReact, FaNode,   } from 'react-icons/fa'
import { SiPostgresql } from 'react-icons/si'
import { TagList } from './StyledElements'

const Project = styled.div`
    figure {
        border-radius: 1rem;
        position: relative;
        height: 250px;
        box-shadow: var(--shadow-light);
        overflow: hidden;
    };

    .link-btn {
        position: absolute;
        bottom: 0rem;
        font-size: 2rem;
        border-radius: 100px;
    };

    .right {
        background-color: rgba(20, 20, 20, 0.8);
        right: 0rem;
        color: white;
        font-size: 2rem;
        border-radius: 100px;
        height: 2.5rem;
        width: 2.5rem;
        display: flex;
        align-items: center;
        justify-content: center;
    }

    .left{
        font-size: 1.2rem;
        background-color: rgba(20, 20, 20, 0.8);
        color: white;
        text-decoration: none;
        padding: .2rem .6rem;
        padding-left: 2rem;

        display: inline-block;
        left: -1rem;
        height: auto;
        width: auto;
    }
    .right:hover,
    .left:hover {
        background-color: black;
    }
`

const Img = styled(Thumbnail)`
    grid-area: img;
height: 100%;`



const Title = styled(H3)`
    display: inline-block;
    margin-top: 1rem;
    font-size: 1.4rem;
`
const Technologies = styled.div`

`

const Description = styled.div`

`

export default function ProjectCard ({project}) {
    const {title, description, imageURL, liveLink, gitLink, tags} = project;
    console.log(project)
    return (
        <Project>
            <figure>
                <Img src={imageURL} />
                <a className="link-btn right" target="_blank" rel="noopener noreferrer" href={gitLink}><FaGithub /></a>
                <a className="link-btn left" target="_blank" rel="noopener noreferrer" href={liveLink}>Live Demo</a>
            </figure>           
                <Title>{title}</Title>
            <TagList tags={["HTML", "SCSS", "JS"]} />

            <Description>{description}</Description>
        </Project>
    )
}
