import React from 'react'
import styled, { css } from 'styled-components'
import { Thumbnail, H3 } from './StyledElements'

const Project = styled.div`
    figure {
        border-radius: 1rem;
        position: relative;
        overflow: hidden;
        box-shadow: var(--shadow-light)

    }
`

const Img = styled(Thumbnail)`
    grid-area: img;
    height: 250px;
`

const LinkBtn = styled.span`
    background: transparent;
    a {
        /* position: absolute; */
        bottom: 0;
        background-color: black;
        color: white;
        padding: .2rem 1rem;
        border-radius: 1rem;


        ${({left}) => {
            if( left ) {
                return css`
                left: -1rem;
                padding-left: 2rem;
                `
            } else { return css`

                right: -1rem;
                padding-right: 2rem;
                `}

        }}


        }

    }

`


const Title = styled(H3)`
    display: inline-block;
`
const Technologies = styled.div`

`

const Description = styled.div`

`

export default function ProjectCard ({project}) {
    const {title, description, imageURL, liveLink, gitLink} = project;
    console.log(project)
    return (
        <Project>
            <figure>
                <Img src={imageURL} />
                    <LinkBtn left ><a target="_blank" rel="noopener noreferrer" href={liveLink}>Live Demo</a></LinkBtn>
                    <LinkBtn righ ><a target="_blank" rel="noopener noreferrer" href={gitLink}>Github</a></LinkBtn>
            </figure>
            <div>

                <Title>{title}</Title>
                <LinkBtn left ><a target="_blank" rel="noopener noreferrer" href={liveLink}>Live Demo</a></LinkBtn>
                <LinkBtn righ ><a target="_blank" rel="noopener noreferrer" href={gitLink}>Github</a></LinkBtn>

            </div>
            <Technologies></Technologies>

            <Description>{description}</Description>
        </Project>
    )
}
