import styled from 'styled-components'

export const Button = styled.button.attrs(props => {
    let {fg, bg} = props.theme
    let size = '10px';
    if (props.big) {size = '15px'}
    if (props.small) {size = '5px'}

    return {
        size,
        fg,
        bg

    }
    })`
    padding: ${props => props.size};
    color: ${props => props.primary ? props.theme.bg : props.theme.fg};
    background-color: ${props => props.primary ? props.theme.fg :  props.theme.bg};
    border: 2px solid ${props => props.theme.fg};
    cursor: pointer;
    border-radius: 100px;
    border: none;

    `
