import styled from 'styled-components'

export const DividerVert = styled.div`
    width: 100%;
    margin: 1rem;
    height: 2px;
    background-color: black;
`

export const Thumbnail = styled.img`
    width: 100%;
    height: 100%;
    border-radius: 1rem;
    box-shadow: var(--shadow-light);
    object-fit: cover;
`

export const Tag = styled.li`

    border-left: 10px solid rgba(0, 0, 0, 0.377);
	color: black;
	border-radius: 10px 60px 60px 10px;
	box-shadow: var(--shadow-main);
	font-size: .8rem;
	font-weight: 700 ;
	padding: 0 .7rem;
	font-weight: 700;
	background: var(--gradient-main);
	margin: 0.5rem;
`

const Tags = styled.ul`
	display: flex;
	flex-wrap: wrap;
	align-items: center;
	list-style: none;
	margin: 0;
	padding: 0;
	margin-left: -0.5rem;
`

export const TagList = ({tags}) => {

    return (
        <Tags>
            {tags.map((tag, i) => (
                <Tag key={i}>{tag}</Tag>
            ))}
        </Tags>
    )
}
export const H2 = styled.h2`
font-size: 2rem;
`
export const H3 = styled.h3`

`
