import React from 'react'
import { Thumbnail, H2, H3 } from './StyledElements'
import styled from 'styled-components'

const ProjectWrapper = styled.div`
    width: 100%;
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-template-areas: "img meta" "description description";

`



const ProjectsWrapper = styled.div`

`
const Thumb = styled(Thumbnail)`
    grid-area: img;
`



export default function ({ projects }) {
    return (
        <ProjectsWrapper>
            <H2>Recent Work</H2>
            {projects.map(project => (
                <ProjectWrapper>
                    <H3>{project.title}</H3>
                    {project.description}
                    <figure>
                        <Thumb src={project.imageURL}/>

                    </figure>
                </ProjectWrapper>
            ))}
        </ProjectsWrapper>
    )
}
