import React from 'react'
import styled, { css } from 'styled-components'
import { Thumbnail, H3 } from './StyledElements'

const Project = styled.div`
    figure {
        position: relative;
        overflow: hidden;

    }
`

const Img = styled(Thumbnail)`
    grid-area: img;
    height: 250px;
`

const LinkBtn = styled.a`

    a {
        position: absolute;
        bottom: 0;
        background-color: black;
        color: white;
        right: 0;

        ${({left}) => {
            if( left ) {
                return css`
                left: 0;
                `
            }
        } }

    }

`
const Title = styled(H3)`

`
const Technologies = styled.div`

`

const Description = styled.div`

`

export default function ProjectCard ({project}) {
    const {title, description, imageURL, liveLink, gitLink} = project;
    console.log(project)
    return (
        <Project>
            <figure>
                <Img src={imageURL} />
                <LinkBtn left ><a href={liveLink}>Live Demo</a></LinkBtn>
                <LinkBtn righ ><a href={gitLink}>Live Demo</a></LinkBtn>
            </figure>
            <Title>{title}</Title>

            <Technologies></Technologies>

            <Description>{description}</Description>
        </Project>
    )
}
