import React from 'react'
import styled from 'styled-components'
import { DividerVert } from './StyledElements'

const HeaderSty = styled.header`
    color: black;
`

const Img = styled.img`
 height: 100px;
 width: 100px;
 border-radius:100px;
 box-shadow: var(--shadow-light);
`

const Nav = styled.nav`
    display: flex;
    flex-direction: column;

    @media(max-width: 700px) {
        flex-direction: row;

    }
`

const H1 = styled.h1`
`

const SubHeader = styled.p`
    font-size: 1.3rem;
    color: var(--color-secondary);

`
const navItems = [
    {
        name: 'Projects',
        linkTo: '#projects'
    },
    {
        name: 'About me',
        linkTo: '#about-me'
    },
    {
        name: 'Contanct',
        linkTo: '#contact'
    },
    {
        name: 'Resume',
        linkTo: '#projects'
    }

]

export default function Header() {


    return (
        <HeaderSty>
            <Img src="images/avatar.jpg"/>
            <H1>I'm √Åron</H1>
            <SubHeader>I like to write JavaScript</SubHeader>
            <Nav>
                {navItems.map(navItem => (<li><a href={navItem.linkTo}>{navItem.name}</a></li>))}
            </Nav>
        </HeaderSty>
    )
}
